package Controller;

import model.Customer;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;

@WebServlet(name = "CustomerController", urlPatterns = "/customer")
public class CustomerController extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        Customer[] customers = new Customer[5];

        customers[0] = new Customer("phúc", "14/02/95", "quảng bình", "data:image/jpeg;base64,");
        customers[1] = new Customer("tài", "14/02/96", "thanh hóa", "data:image/jpeg;base64,");
        customers[2] = new Customer("trí", "14/02/97", "quảng trị", "data:image/jpeg;base64,");
        customers[3] = new Customer("phương", "14/02/98", "quảng trị", "data:image/jpeg;base64,");
        customers[4] = new Customer("luận", "14/02/99", "đà nẵng", "data:image/jpeg;base64,");

        request.setAttribute("listCustomer", customers);
        request.getRequestDispatcher("customer.jps").forward(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {

    }
}
